INSERT INTO `wp_posts` VALUES("404", "1", "2022-04-24 17:04:38", "2022-04-24 15:04:38", "<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"100%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:100%\"><!-- wp:cover {\"url\":\"http://localhost:8888/wordpress/wp-content/uploads/2022/04/Mountains-7418-1-1.mp4\",\"id\":71,\"dimRatio\":50,\"backgroundType\":\"video\",\"minHeight\":500,\"contentPosition\":\"center center\",\"isDark\":false} -->\n<div class=\"wp-block-cover is-light\" style=\"min-height:500px\"><span aria-hidden=\"true\" class=\"wp-block-cover__gradient-background has-background-dim\"></span><video class=\"wp-block-cover__video-background intrinsic-ignore\" autoplay muted loop playsinline src=\"http://localhost:8888/wordpress/wp-content/uploads/2022/04/Mountains-7418-1-1.mp4\" data-object-fit=\"cover\"></video><div class=\"wp-block-cover__inner-container\"><!-- wp:paragraph {\"align\":\"center\",\"style\":{\"typography\":{\"fontSize\":\"75px\",\"textTransform\":\"capitalize\",\"fontStyle\":\"italic\",\"fontWeight\":\"400\"}},\"textColor\":\"white\"} -->\n<p class=\"has-text-align-center has-white-color has-text-color\" style=\"font-size:75px;font-style:italic;font-weight:400;text-transform:capitalize\">Chalets &amp; Caviar</p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:cover --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns {\"className\":\"block-padding\"} -->\n<div class=\"wp-block-columns block-padding\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"className\":\"text-white\"} -->\n<h2 class=\"text-white\">Chalet à louer</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"style\":{\"typography\":{\"fontSize\":\"15px\"}}} -->\n<p style=\"font-size:15px\">Envie d’escapade le temps d’une semaine ou plus? Découvrez notre sélection de chalets à louer en couple, en famille ou entre amis, quelle que soit la saison.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link\">Louer</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"className\":\"text-white\"} -->\n<h2 class=\"text-white\">Chalet à acheter</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"style\":{\"typography\":{\"fontSize\":\"15px\"}}} -->\n<p style=\"font-size:15px\">Offrez-vous le calme et la tranquillité à l’année au pied des pistes de Courchevel. Consultez notre catalogue de chalets de prestige.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link\">Acheter</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:heading {\"className\":\"text-white\"} -->\n<h2 class=\"text-white\">Découvrir</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"style\":{\"typography\":{\"fontSize\":\"15px\"}}} -->\n<p style=\"font-size:15px\">Station de ski de renommée mondiale, Courchevel fait partie du domaine skiable des Trois-Vallées. Au-delà des pistes, découvrez ici le charme des cinq villages mythiques.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:buttons -->\n<div class=\"wp-block-buttons\"><!-- wp:button -->\n<div class=\"wp-block-button\"><a class=\"wp-block-button__link\">Découvrir</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:image {\"align\":\"center\",\"id\":50,\"width\":719,\"height\":225,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<div class=\"wp-block-image\"><figure class=\"aligncenter size-full is-resized\"><img src=\"http://localhost:8888/wordpress/wp-content/uploads/2022/04/15632617541681_image1-removebg-preview-1.png\" alt=\"\" class=\"wp-image-50\" width=\"719\" height=\"225\"/></figure></div>\n<!-- /wp:image -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"style\":{\"typography\":{\"fontSize\":\"25px\"}},\"className\":\"text-white\"} -->\n<p class=\"has-text-align-center text-white\" style=\"font-size:25px\">Effectuer une réservation</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"className\":\"text-white\"} -->\n<p class=\"has-text-align-center text-white\">Les réservations s’effectuent sur des plages d’une semaine minimum. Pour toute demande particulière, <a href=\"http://localhost:8888/wordpress/contact/\" data-type=\"URL\" data-id=\"http://localhost:8888/wordpress/contact/\">veuillez nous contacter.</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns {\"className\":\"back-color\"} -->\n<div class=\"wp-block-columns back-color\"><!-- wp:column {\"verticalAlignment\":\"center\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:image {\"id\":274,\"sizeSlug\":\"large\",\"linkDestination\":\"none\",\"className\":\"is-style-default\"} -->\n<figure class=\"wp-block-image size-large is-style-default\"><img src=\"http://localhost:8888/wordpress/wp-content/uploads/2022/04/Chalet-3-2000x1328.jpeg\" alt=\"\" class=\"wp-image-274\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"verticalAlignment\":\"center\"} -->\n<div class=\"wp-block-column is-vertically-aligned-center\"><!-- wp:shortcode -->\n[everest_form id=\"234\"]\n<!-- /wp:shortcode --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:group {\"className\":\"back-color\"} -->\n<div class=\"wp-block-group back-color\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"className\":\"about\"} -->\n<div class=\"wp-block-column about\"><!-- wp:heading {\"className\":\"about\"} -->\n<h2 class=\"about\">Qui sommes-nous ?</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"left\"} -->\n<p class=\"has-text-align-left\">L’agence immobilière Chalets et Caviar est à vos côtés depuis plus de 10 ans afin de vous proposer une sélection de chalets de prestige. Nous avons fait du haut-de-gamme notre spécialité pour faire de vos séjours à la montagne des moments inoubliables.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"className\":\"about\"} -->\n<div class=\"wp-block-column about\"><!-- wp:image {\"id\":241,\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"className\":\"is-style-default\"} -->\n<figure class=\"wp-block-image size-full is-style-default\"><img src=\"http://localhost:8888/wordpress/wp-content/uploads/2022/04/winter-4727668_1920-768x512-1.jpeg\" alt=\"\" class=\"wp-image-241\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->\n\n<!-- wp:paragraph {\"align\":\"center\",\"className\":\"gamme-service\"} -->\n<p class=\"has-text-align-center gamme-service\">Large gamme de services supplémentaires disponibles</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":317,\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"className\":\"is-style-default landing-page-services-icons\"} -->\n<div class=\"wp-block-image is-style-default landing-page-services-icons\"><figure class=\"aligncenter size-full\"><img src=\"http://localhost:8888/wordpress/wp-content/uploads/2022/04/alpine-2-100x100-1.png\" alt=\"\" class=\"wp-image-317\"/><figcaption>Ski</figcaption></figure></div>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":315,\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"className\":\"is-style-default landing-page-services-icons\"} -->\n<div class=\"wp-block-image is-style-default landing-page-services-icons\"><figure class=\"aligncenter size-full\"><img src=\"http://localhost:8888/wordpress/wp-content/uploads/2022/04/relax-1-100x100-1.png\" alt=\"\" class=\"wp-image-315\"/><figcaption>Spa</figcaption></figure></div>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":314,\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"className\":\"is-style-default landing-page-services-icons\"} -->\n<div class=\"wp-block-image is-style-default landing-page-services-icons\"><figure class=\"aligncenter size-full\"><img src=\"http://localhost:8888/wordpress/wp-content/uploads/2022/04/coffee-1-100x100-1.png\" alt=\"\" class=\"wp-image-314\"/><figcaption>Petit-déjeuner</figcaption></figure></div>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":316,\"width\":101,\"height\":101,\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"className\":\"is-style-default landing-page-services-icons\"} -->\n<div class=\"wp-block-image is-style-default landing-page-services-icons\"><figure class=\"aligncenter size-full is-resized\"><img src=\"http://localhost:8888/wordpress/wp-content/uploads/2022/04/tray-1-100x100-1.png\" alt=\"\" class=\"wp-image-316\" width=\"101\" height=\"101\"/><figcaption>Restaurants</figcaption></figure></div>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"align\":\"center\",\"id\":313,\"sizeSlug\":\"full\",\"linkDestination\":\"none\",\"className\":\"is-style-default landing-page-services-icons\"} -->\n<div class=\"wp-block-image is-style-default landing-page-services-icons\"><figure class=\"aligncenter size-full\"><img src=\"http://localhost:8888/wordpress/wp-content/uploads/2022/04/cocktail-1-100x100-1.png\" alt=\"\" class=\"wp-image-313\"/><figcaption>Bar</figcaption></figure></div>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:group {\"className\":\"avis-clients\"} -->\n<div class=\"wp-block-group avis-clients\"><!-- wp:group -->\n<div class=\"wp-block-group\"><!-- wp:heading {\"textAlign\":\"center\",\"className\":\"text-white \"} -->\n<h2 class=\"has-text-align-center text-white\">Ce que nos clients ont pensé</h2>\n<!-- /wp:heading --></div>\n<!-- /wp:group -->\n\n<!-- wp:columns {\"className\":\"avis-clients-text \"} -->\n<div class=\"wp-block-columns avis-clients-text\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"className\":\"blockquote\"} -->\n<p class=\"blockquote\">Nous avons passé un séjour inoubliable. Accueil et services au top, notamment le petit-déjeuner qui était délicieux. Nous reviendrons sans hésiter !</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Sophie Marceau</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"className\":\"blockquote\"} -->\n<p class=\"blockquote\">Le point fort de ce chalet est son emplacement. Idéalement situé, il se trouve à proximité des pistes, des commerces et du centre de la station </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Courchevel 1850.Pierre Duval</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"className\":\"blockquote\"} -->\n<p class=\"blockquote\">Un séjour parfait pour notre anniversaire de mariage. Les extras proposés ont fait toute la différence pour rendre nos vacances inoubliables.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Louïse Saint-Martin</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->", "Accueil", "", "inherit", "closed", "closed", "", "7-revision-v1", "", "", "2022-04-24 17:04:38", "2022-04-24 15:04:38", "", "7", "http://localhost:8888/wordpress/?p=404", "0", "revision", "", "0");
